mapper.select:
SELECT COUNT(U.ID) FROM USERINFO U, GROUPS G, GROUP_MEMBER GM WHERE 1 = 1
AND GM.GROUPID = #{groupId} AND G.ID = #{groupId}
AND G.NAME = #{groupName}
AND GM.TYPE = 'USER' AND GM.GROUPID = G.ID AND GM.MEMBERID = U.ID

SELECT COUNT(U.ID) FROM USERINFO U WHERE U.ID NOT IN( SELECT GM.MEMBERID FROM GROUPS G,GROUP_MEMBER GM WHERE 1 = 1
AND GM.GROUPID = #{groupId} AND G.ID = #{groupId}
AND G.NAME = #{groupName}
AND GM.TYPE = 'USER' AND GM.GROUPID = G.ID )

SELECT DISTINCT IG.* FROM GROUPS IG, GROUPS G, GROUP_MEMBER GM WHERE 1 = 1 AND GM.GROUPID = G.ID AND GM.MEMBERID = IG.ID AND GM.TYPE = 'GROUP'
AND GM.GROUPID = #{groupId} AND G.ID = #{groupId}
AND G.NAME = #{groupName}

SELECT COUNT(G.ID) FROM GROUPS IG, GROUPS G, GROUP_MEMBER GM WHERE 1 = 1 AND GM.GROUPID = G.ID AND GM.MEMBERID = IG.ID AND GM.TYPE = 'GROUP'
AND GM.GROUPID = #{groupId} AND G.ID = #{groupId}
AND G.NAME = #{groupName}
