mapper.update:
    	UPDATE RESOURCES SET STATUS	=	'2'  
    		WHERE	1	=	1

			AND	ID	=	#{id}


			ADN	NAME	=	#{name}


			ADN	STATUS	=	'1'


			ADN	XPATH	=	#{xPath}

	UPDATE RESOURCES SET STATUS	=	'2'  
		 WHERE ID IN

    	#{selectId}

mapper.delete:
    	DELETE FROM RESOURCES WHERE 1 = 1 	AND
			(
				ID	=	#{id} 
				OR  PID =	#{id} 
				OR  XPATH  LIKE 	'%${id}%' 
			)
